const ACTIVE = 'active';
const CLOSED = 'closed';
const DETECTED = 'detected';
const FOLLOW_SCHEDULE = 'follow schedule';
const GOOD = 'good';
const HEATING = 'heating';
const LOCKED = 'locked';
const MUTED = 'muted';
const OFF = 'off';
const ON = 'on';
const OPEN = 'open';
const PRESENT = 'present';
const PUSHED = 'pushed';
const SLEEPING = 'sleeping';
const TOUCHED = 'touched';
const WET = 'wet';

var v = {
    'value' : null,
    'isBinary' : null
};

if(msg.name == "acceleration") {
    v.value = (msg.value == ACTIVE ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "alarm") {
    v.value = (msg.value != OFF ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "button") {
    v.value = (msg.value != PUSHED ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "carbonMonoxide") {
    v.value = (msg.value == DETECTED ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "consumableStatus") {
    v.value = (msg.value == GOOD ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "contact") {
    v.value = (msg.value == OPEN ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "door") {
    v.value = (msg.value != OPEN ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "lock") {
    v.value = (msg.value == LOCKED ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "motion") {
    v.value = (msg.value == ACTIVE ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "mute") {
    v.value = (msg.value == MUTED ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "presence") {
    v.value = (msg.value == PRESENT ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "shock") {
    v.value = (msg.value == DETECTED ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "sleeping") {
    v.value = (msg.value == SLEEPING ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "smoke") {
    v.value = (msg.value == DETECTED ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "sound") {
    v.value = (msg.value == DETECTED ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "switch") {
    v.value = (msg.value == ON ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "tamper") {
    v.value = (msg.value == DETECTED ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "thermostatMode") {
    v.value = (msg.value != OFF ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "thermostatFanMode") {
    v.value = (msg.value != OFF ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "thermostatOperatingState") {
    v.value = (msg.value == HEATING ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "thermostatSetpointMode") {
    v.value = (msg.value != FOLLOW_SCHEDULE ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "touch") {
    v.value = (msg.value != TOUCHED ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "optimisation") {
    v.value = (msg.value == ACTIVE ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "windowFunction") {
    v.value = (msg.value == ACTIVE ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "water") {
    v.value = (msg.value == WET ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "windowShade") {
    v.value = (msg.value == CLOSED ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "threeAxis") {
    var vz = msg.value.split(',');
    var x = vz[0];
    var y = vz[1];
    var z = vz[2];
    v.value = {
        x: vz[0],
        y: vz[1],
        z: y[2]
    };
    v.isBinary = false;
}
else if (msg.value.match(/.*[^0-9\.,-].*/)) { // match if any characters are not digits, period, comma, or hyphen.
    v.value = '"' + msg.value + '"';
    v.isBinary = false;
}

// Catch any other general numerical event (carbonDioxide, power, energy, humidity, level, temperature, ultravioletIndex, voltage, etc).
else {
    v.value = Number(msg.value);
    v.isBinary = false;
}

if (v.isBinary)
{
    msg.payload = [
        {
            measurement: msg.name,
            fields: {
                value: msg.value,
                valueBinary: v.value
            },
            tags:{
                hub: msg.hubId ? msg.hubId : "HE1",
                deviceId: msg.deviceId,
                displayName: msg.displayName,
                unit: msg.unit
            },
            timestamp: new Date()
        }
    ]    
}
else
{
    msg.payload = [
        {
            measurement: msg.name,
            fields: {
                value: v.value,
            },
            tags:{
                hub: msg.hubId ? msg.hubId : "HE1",
                deviceId: msg.deviceId,
                displayName: msg.displayName,
                unit: msg.unit
            },
            timestamp: new Date()
        }
    ]
}
//console.log(util.inspect(msg, {showHidden: false, depth: null}))

return msg;
